import Box from '@material-ui/core/Box';
import CircularProgress from '@material-ui/core/CircularProgress';

interface LoadingSpinnerProps {
    position ?: string;
    level ?: number;
}

const LoadingSpinner = ({position, level} : LoadingSpinnerProps) => {

    const style = () => {
        switch(position) {
            case `mr-${level}`:
                return {marginRight: `${level}rem`}
        }
    }

    return (
        <Box style={style()}>
            <CircularProgress />
        </Box>
    )
}

export default LoadingSpinner;
