import globalVars from "../../library/globalVariables";

const ClientForm = ({ fields, onChange, newClient }: any) => {
  const inputs = fields.map((e: any) => {
    if (e.fieldName !== globalVars.permanentFields["Date added"] && e.fieldName !== globalVars.permanentFields["Last modified"]) {
      if (e.fieldType === "text") {
        return (
          <div key={e.fieldName}>
            <label htmlFor={e.fieldName}>
              {e.fieldName}
            </label>
            <input
              type="text"
              id={e.fieldName}
              autoFocus={e.fieldName === globalVars.permanentFields.fistName}
              name={e.fieldName}
              onChange={() => onChange(e.fieldType, event)}
              defaultValue={newClient[e.fieldName]}
            />
          </div>
        );
      } else if (e.fieldType === "number") {
        return (
          <div key={e.fieldName}>
            <label htmlFor={e.fieldName}> 
             {e.fieldName} 
            </label>
            <input
              id={e.fieldName}
              type="number"
              name={e.fieldName}
              onChange={() => onChange(e.fieldType, event)}
              defaultValue={newClient[e.fieldName]}
            />
          </div>
        );
      } else if (e.fieldType === "select") {
        return (
          <div key={e.fieldName}>
            <label htmlFor={e.fieldName}>
              {e.fieldName}
            </label>
            <select
              id={e.fieldName}
              name={e.fieldName}
              onChange={() => onChange(e.fieldType, event)}
              defaultValue={newClient[e.fieldName]}
            >
              <option>{globalVars.blankOption}</option>
              {e.fieldOptions.map((e: any) => {
                return (
                  <option value={e.value} key={e.id}>
                    {e.value}
                  </option>
                );
              })}
            </select>
          </div>
        );
      }
    }
  });
  return <div>{inputs}</div>;
};

export default ClientForm;
