import { useState } from "react";
import axios from "axios";
import TextInput from "./TextInput";
import LoginButton from "./Button";
import globalVars from "../../library/globalVariables";
import languages from "../../library/languages";
import stringMethods from "../../library/stringMethods";

const RegisterForm = () => {
    const [regUser, setRegUser] = useState({username : "", password : "", passwordAgain : ""});

    const onChange = (e) =>Â {
        e.target.name === "username"
            ? setRegUser({...regUser, username: e.target.value})
            : setRegUser({...regUser, password: e.target.value})
    }

    const onRegister = async() => {
        const userRes = await axios({
            method: "POST",
            data: regUser,
            url: `${globalVars.serverURL}/users`,
            responseType: "json"
        })
        const userData = await userRes.data;
        if(userData.msg === "Success") {
            alert(languages.en.success)
        } else {
            alert(languages.en.somethingWentWrong)
        }
    }

    return (
        <>  
            <h2>Register</h2>
            <form>
                <TextInput onChange={onChange} type="text" value={regUser.username} text="username" title={new stringMethods(languages.en.username).firstCharUpperCase().getString()} />
                <TextInput onChange={onChange} type="password" value={regUser.password} text="password" title={languages.en.password} />
                <TextInput onChange={onChange} type="password" value={regUser.passwordAgain} text="password" title={new stringMethods(languages.en.repeatPassword).firstCharUpperCase().getString()}/>
                <LoginButton onClick={onRegister} text={languages.en.register} />
            </form>
        </>
    )
}

export default RegisterForm;
