import stringMethods from "../library/stringMethods";

const CustomClientFields = ({ fields }: any) => {
  const customFields = fields
    .filter(({ fieldPermanent }: any) => !fieldPermanent)
    .map(({ fieldName, _id, fieldType, fieldOptions }: any) => {
      const customFieldNameFormated = new stringMethods(fieldName)
        .camelStringToText()
        .firstCharUpperCase()
        .getString();

      const customFieldTypeFormated = new stringMethods(fieldType)
        .camelStringToText()
        .getString();

      const optionsMap = () => {
      return fieldOptions.map((e : string ) => {
        const optionFormatedText = new stringMethods(e)
            .firstCharUpperCase()
            .getString()

        const optionFormatedKex = new stringMethods(e)
            .textToCamelString()
            .replaceStringDiacritics()
            .getString()

      return <li key={e}>{optionFormatedText}</li>})
      }

      return (
        <div key={_id}>
          <p>{customFieldNameFormated}</p>
          <p>({customFieldTypeFormated})</p>
          {fieldType === "select"
          ? <ul>{optionsMap()}</ul>
          : null}
        </div>
      );
    });

  return (
    <div>
      <h2>Custom Client Fields</h2>
      <div>{customFields}</div>
    </div>
  );
};

export default CustomClientFields;
