import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import Link from "next/link";
import axios from "axios";
import Header from "../components/Header";
import stringMethods from "../library/stringMethods";
import titleSubText from "../library/globalVariables";


//TODO: SORTOVÁNÍ
//TODO: TABULKA DYNAMICKÉ ZÁHLAVÍ
//TODO: PŘIDAT NOVÉHO KLIENTA

const ClientsList = (props: any) => {
  return props.clients.map((e:any) => (
    <tr key={e._id}>
      <td>{e.dateAdded}</td>
      <td>{e.name}</td>
      <td>{e.age}</td>
      <td>{e.address}</td>
      <td>
        <Link href="/clients/[id]" as={`/clients/${e._id}`}>
          <a>Go to client</a>
        </Link>
      </td>
    </tr>
  ));
};

const Clients = (props: any) => {
  const router = useRouter();
  const [clients, setClientList] = useState([]);
  const [numero, setNumero] = useState(0);

  useEffect(() => {
    setClientList(props.data);
  }, [props.data]);

  useEffect(() => {
    const title = new stringMethods(router.pathname)
      .removeSlash()
      .firstCharUpperCase()
      .addStringToEnd(titleSubText)
      .text;
    document.title = title;
  }, [router]);

  const reverseOrder = () => {
    setClientList(clients.reverse());
    console.log(clients);
  }

  const count = () => {
    setNumero(1);
  }

  if (clients.length === 0) {
    return "Loading";
  }
  return (
    <div>
      <Header />
      <h1>All clients</h1>
      <table>
        <thead>
          <tr>
            <th><button onClick={reverseOrder}>Date Added</button></th>
            <th><button onClick={count}>Name</button></th>
            <th>Age</th>
            <th>Address</th>
          </tr>
        </thead>
        <tbody>
          <ClientsList clients={clients} />
        </tbody>
      </table>
    </div>
  );
};

Clients.getInitialProps = async () => {
  const res = await axios({
    method: "get",
    url: "http://localhost:8080/api/clients",
    responseType: "json"
  });
  const data = await res.data;
  return {
    data
  };
};

export default Clients;
