import { useEffect, useState } from "react";

const SelectFieldOptions = ({ inputsNumber, getInputs }: any) => {
  const [ optionValue, setOptionValue ] = useState({text: "", id: 0});

  useEffect(() => {
    getInputs(optionValue)
  })

  const onChange = (e : any) => {
    setOptionValue({...optionValue, text: e.target.value, id: e.target.id})

  }

  let items: any = [];

    for (let i = 0; i < inputsNumber; i++) {
      items.push(
        <div key={i}>
          <input type="text" onChange={onChange} value={optionValue.text} id={i.toString()}/>
          <button id={i.toString()}>
            X
          </button>
        </div>
      );
    }

  return <div>{items}</div>;
};
export default SelectFieldOptions;
