import globalVars from "../../library/globalVariables";
import { TextField, Select, FormControl, MenuItem } from "@material-ui/core";

const SingleClientData = ({ fieldsData, client, onInputChange }: any) =>
  fieldsData.map(field => {
    switch (field.fieldType) {
      case "text":
        return (
          <div key={field.fieldName}>
            <TextField
              name={field.fieldName}
              label={field.fieldName}
              margin="normal"
              value={client[field.fieldName]}
              onChange={onInputChange}
              disabled={
                field.fieldName === "Date added" ||
                field.fieldName === "Last modified"
              }
            />
          </div>
        );
      case "number":
        return (
          <div key={field.fieldName}>
            {field.fieldName}
            <input
              type="number"
              name={field.fieldName}
              value={client[field.fieldName]}
              onChange={onInputChange}
            />
          </div>
        );
      case "select":
        return (
          <FormControl key={field.fieldName}>
            {field.fieldName}
            <Select
              name={field.fieldName}
              value={client[field.fieldName]}
              onChange={onInputChange}
            >
              <MenuItem key="---">{globalVars.blankOption}</MenuItem>
              {field.fieldOptions.map(option => (
                <MenuItem key={option.id}>{option.value}</MenuItem>
              ))}
            </Select>
          </FormControl>
        );
    }
  });

export default SingleClientData;
