import { useState } from "react";
import AddOrEditField from "../components/AddOrEditField";
import axios from "axios";
import globalVars from "../library/globalVariables";
import CustomFields from "../components/CustomFields";

const CustomClientFields = ({ fields, refreshList }: any) => {
  const [editedField, setEditedField] = useState({
    fieldName: "",
    fieldType: "text",
    fieldPermanent: false,
    fieldOptions: []
  })

  const saveEditedField = async () => {
    const res = await axios({
      method: "post",
      url: `${globalVars.serverURL}/fields/`,
      data: editedField,
      responseType: "json"
    });
    const data = await res.data;
    data.msg === "Success" ? changeDisplayComponent() : null;
    console.log(editedField);
    refreshList();
  };

  const deleteField = async (event : any) =>Â {
    const id = event.target.id;
    const res = await axios({
        method: "delete",
        url:`${globalVars.serverURL}/fields/${id}`,
        responseType: "json"
    })
    const resData = await res.data;
    resData.msg === "Success" ? refreshList() : null;
  }

  // const setupEditedField = (obj ? : any) => {
  //   setEditedField(obj);
  // }

  return (
    <div>
      <h2>Custom Client Fields</h2>
      <CustomFields deleteField={deleteField} fields={fields}/>
      <AddOrEditField editedField={editedField} refreshList={refreshList} saveEditedField={saveEditedField}/>
    </div>
  );
};

export default CustomClientFields;
