import { useState } from "react";
import AddOrEditField from "../components/AddOrEditField";
import axios from "axios";
import globalVars from "../library/globalVariables";
import uniqid from "uniqid";
import CustomFields from "./CustomFieldsList";

const CustomClientFields = ({ fields, refreshList }: any) => {
  const [editedField, setEditedField] = useState({
    fieldName: "",
    fieldType: "text",
    fieldPermanent: false,
    fieldOptions: [{}]
  })

  const saveEditedField = async () => {
    const res = await axios({
      method: "post",
      url: `${globalVars.serverURL}/fields/`,
      data: editedField,
      responseType: "json"
    });
    const data = await res.data;
    // data.msg === "Success" ? changeDisplayComponent() : null;
    console.log(data);
    refreshList();
  };

  const deleteField = async (event : any) =>Â {
    const id = event.target.id;
    const res = await axios({
        method: "delete",
        url:`${globalVars.serverURL}/fields/${id}`,
        responseType: "json"
    })
    const resData = await res.data;
    resData.msg === "Success" ? refreshList() : null;
  }

  const onNameChange = (event: any) => {
    setEditedField({ ...editedField, fieldName: event.target.value});
    console.log(editedField);
  };

  const onSelectChange = (event: any) => {
    setEditedField({
      ...editedField,
      fieldType: event.target.options[event.target.options.selectedIndex].value
    });
  };

  const handleOptionSpawn = () => {
    setEditedField({
      ...editedField,
      fieldOptions: [...editedField.fieldOptions, { id: uniqid(), value: "" }]
    });
  };

  const onOptionChange = (event: any) => {
    setEditedField({
      ...editedField,
      fieldOptions: editedField.fieldOptions.filter((e: any) =>
        event.target.id === e.id ? (e.value = event.target.value) : e
      )
    });
  };

  const onOptionDelete = (event: any) => {
    setEditedField({
      ...editedField,
      fieldOptions: editedField.fieldOptions.filter((e: any) =>
        event.target.id !== e.id ? e : null
      )
    });
  };

  // const setupEditedField = (obj ? : any) => {
  //   setEditedField(obj);
  // }

  return (
    <div>
      <h2>Custom Client Fields</h2>
      <CustomFields deleteField={deleteField} fields={fields}/>
      <AddOrEditField editedField={editedField} refreshList={refreshList} saveEditedField={saveEditedField} onNameChange={onNameChange} onSelectChange={onSelectChange} handleOptionSpawn={handleOptionSpawn} onOptionChange={onOptionChange} onOptionDelete={onOptionDelete}/>
    </div>
  );
};

export default CustomClientFields;
