import languages from "../../../library/languages";
import { Typography, Box, ListItem, List, IconButton } from "@material-ui/core";
import HighlightOffIcon from '@material-ui/icons/HighlightOff';
import { useEffect } from "react";

const WebFormList = ({ state, removeFromList, dispatch }) => {
  // const list = [];
  const list = state.filter(el => {
    return el.fieldInForm
  }).sort((a, b) => b.order - a.order)


  state.map(
    ({
      _id,
      fieldName,
      fieldInForm,
      fieldFormVisible,
      fieldOptions,
      fieldType,
      // order
    }) => {
      const strFormVisible = fieldFormVisible
        ? ` - ${languages.en.visibleInForm}`
        : null;

      const strFormNotVisible1 = !fieldFormVisible
        ? fieldType === "select"
          ? ` - ${languages.en.hiddenInForm}`
          : null
        : null;

      const strFormNotVisible2 = !fieldFormVisible
        ? fieldType === "select"
          ? fieldOptions.map(e => (e.preselected ? e.value : null)).join("")
          : null
        : null;

      const strFormNotVisible3 = !fieldFormVisible
        ? fieldType === "select"
          ? languages.en.asPreselected
          : null
        : null;

      languages.en.asPreselected;

      fieldInForm
        ? 
            <ListItem key={_id}>
              <Typography>
              <IconButton id={fieldName} onClick={() => removeFromList(dispatch, event)} color="secondary" style={{marginRight: "1rem"}}>
                <HighlightOffIcon/>
              </IconButton>
              <strong>{fieldName}</strong>
              {strFormVisible}
              {strFormNotVisible1} <strong>{strFormNotVisible2}</strong>{" "}
              {strFormNotVisible3}
              </Typography>
            </ListItem>

        : [];
    }
  );

  useEffect(() => {
    console.log(list);
  })

  return (
    <Box mb="2rem">
      <Typography component="h3" variant="h5" gutterBottom>{languages.en.selectedFields}</Typography>
      {state.some(e => e.fieldInForm) ? <List dense></List> : <Typography>{languages.en.noFieldsSelected}</Typography>}
    </Box>
  );
};

export default WebFormList;
