export const handleAddingToWF = (dispatch, state, counter, setCounter, e) => {
  state.map(field => {
    if (field.fieldName === e.target.value) {
      if (field.fieldType !== "select") {
        dispatch({
          type: "addToWF",
          payload: { fieldName: e.target.value, counter }
        });
        setCounter(prevCount => prevCount + 1);
      } else {
        dispatch({
          type: "pauseSelect",
          payload: { fieldName: e.target.value }
        });
      }
    }
  });
};
export const addVisibleSelect = (dispatch, setCounter, counter, e) => {
  dispatch({
    type: "addVisibleSelectWF",
    payload: { fieldName: e.currentTarget.id, counter }
  });
  setCounter(prevCount => prevCount + 1);
};

export const addHiddenSelect = (dispatch, setCounter, counter, e) => {
  dispatch({
    type: "addHiddenSelectWF",
    payload: { optionValue: e.target.value, counter }
  });
  setCounter(prevCount => prevCount + 1);
};

export const removeFromList = (dispatch, e) => {
  dispatch({
    type: "removeFromFW",
    payload: { fieldName: e.currentTarget.id }
  });
};

export const showOptionsOnClick = (dispatch) => {
  dispatch({
    type: "addNotVisibleValue"
  });
};
