import { useEffect, useState } from "react";

const webFormOptionSelect = ({ optionSelect, webFields, addVisibleSelect }) => {

  const [options, setOption] = useState([])

  const showPreselectOptions = (e) => {
    const optionsArr = webFields.map(field =>
        field.fieldType === e.target.id
          ? field.fieldOptions.forEach(option =>
                <option key={option.id} value={option.value}>
                  {option.value}
                </option>
            )
          : null
      );
      setOption(optionsArr);
  }

  useEffect(() => {
      console.log("prerendered");
  })

  const insertIntoForm = webFields.map(field =>
    optionSelect === field.fieldName ? (
      <div key={field.id}>
        <p>
          Should be <strong>{field.fieldName}</strong> visible in form?
        </p>
        <button id={field.fieldName} onClick={addVisibleSelect}>
          YES
        </button>
        <button id={field.fieldName} onClick={showPreselectOptions}>
          NO
        </button>
      </div>
    ) : null
  );

  return (
    <>
      {insertIntoForm}
      <select>{options}</select>
    </>
  );
};

export default webFormOptionSelect;
