import Link from "next/link";
import uniqid from "uniqid";

//TODO Filtrují se i polé které neexistujou na straně body

interface IProps {
  clients: any;
  reverse: boolean;
  sort: string;
  fields: any;
  handleCheckbox: any;
}

const TableBody = ({
  clients,
  reverse,
  sort,
  fields,
  handleCheckbox
}: IProps) => {
  if (fields.some(e => e.fieldName === sort && e.fieldType === "number")) {
    if (reverse) {
      clients.sort((a: any, b: any) => b[sort] - a[sort]);
    } else {
      clients.sort((a: any, b: any) => a[sort] - b[sort]);
    }
  } else {
    if (reverse) {
      clients.sort((a: any, b: any) => (b[sort] > a[sort] ? -1 : 1));
    } else {
      clients.sort((a: any, b: any) => (b[sort] < a[sort] ? -1 : 1));
    }
  }

  const fieldNames = [];
  fields.forEach((e: any) => fieldNames.push(e.fieldName, e.fieldType));

  const tableClients = clients.map((client: any) => {
    const tableItem = () => {
      let items = [];

        for (let key in client) {
          //show only clients data with existing fields
          if(client[key] !== client._id && fieldNames.includes(key)) {

            items.push(<td key={uniqid()}>{client[key]}</td>)
          } else if (!fieldNames.includes(key)) {
            items.push(<td key={uniqid()}></td>)
          }
        }


      for (let key in client) {
        //show only clients data with existing fields
        client[key] !== client._id && fieldNames.includes(key)
          ? items.push(<td key={uniqid()}>{client[key]}</td>)
          : null;
      }
      // add checkbox at first position
      items.unshift(
        <td key={uniqid()}>
          <input
            type="checkbox"
            checked={client.isChecked === undefined ? false : client.isChecked}
            onChange={() => handleCheckbox(client._id)}
          />
        </td>
      );
      // add profile link at last position
      items.push(
        <td key={uniqid()}><Link href="/clients/[id]" as={`/clients/${client._id}`}>
          <a>Go</a>
        </Link></td>
      );
      return items;
    };

    return (
      <tr key={client._id}>
        {tableItem()}
      </tr>
    );
  });
  return tableClients;
};

export default TableBody;
