import { useContext, useState } from "react";
import Link from "next/link";
import languages from "../library/languages";
import UserContext from "./UserContext";
import { createStyles, makeStyles, Theme } from "@material-ui/core/styles";
import Container from "@material-ui/core/Container";
import DashboardIcon from "@material-ui/icons/Dashboard";
import PeopleAltIcon from '@material-ui/icons/PeopleAlt';
import EmailIcon from '@material-ui/icons/Email';
import SettingsIcon from '@material-ui/icons/Settings';
import ExitToAppIcon from '@material-ui/icons/ExitToApp';
import { Grid, Button, Toolbar, AppBar, Box, IconButtonÂ } from '@material-ui/core';
import MenuIcon from '@material-ui/icons/Menu';

const useStyles = makeStyles((theme: Theme) =>
  createStyles({
    menuButton: {
      marginRight: theme.spacing(2),
      color: "white"
    },
    title: {
      flexGrow: 1
    },
    toolBar: {
      padding: 0,
    },
    mobilMenuButton: {
      color: "white",
      display: "none", [theme.breakpoints.down("sm")]: {
        display: "block"
      }
    },
    menu: {
      display: "block", [theme.breakpoints.down("sm")]: {
        display: "none"
      }
    }
  })
);

const Header = () => {
  const classes = useStyles({})
  const user = useContext(UserContext);
  const [mobilMenu, setMobilMenu] = useState(false)

  const toggleMenu = () => {
      setMobilMenu(prevState => !prevState)
  }

  return (
    <AppBar position="fixed">
      <Container maxWidth="lg">
        <Toolbar className={classes.toolBar}>
          <Box display="flex" width="100%" justifyContent="space-between">
            <IconButton onClick={toggleMenu} className={classes.mobilMenuButton}>
                <MenuIcon/>
            </IconButton>
            <Box component="nav" className={classes.menu} display={mobilMenu ? "block" : "none"}>
              <Grid container>
                <Grid item>
              <Link
                href="/dashboard/[key]"
                as={`/dashboard/${user.user.userkey}`}
              >
                <Button startIcon={<DashboardIcon />} className={classes.menuButton}>
                  {languages.en.dashboard}
                </Button>
              </Link>
              </Grid>
              <Grid item>
              <Link href="/clients/[key]" as={`/clients/${user.user.userkey}`}>
                <Button startIcon={<PeopleAltIcon />} className={classes.menuButton}>
                  {languages.en.clients}
                </Button>
              </Link>
              </Grid>
              <Grid item>
              <Link href="/emails/[key]" as={`/emails/${user.user.userkey}`}>
                <Button startIcon={<EmailIcon />} className={classes.menuButton}>
                  {languages.en.emails}
                </Button>
              </Link>
              </Grid>
              <Grid item>
              <Link
                href="/settings/[key]"
                as={`/settings/${user.user.userkey}`}
              >
                <Button startIcon={<SettingsIcon />} className={classes.menuButton}>
                  {languages.en.settings}
                </Button>
              </Link>
              </Grid>
              </Grid>
            </Box>
          </Box>
          <Button
            className={classes.menuButton}
            onClick={() => user.logoutUser()}
            startIcon={<ExitToAppIcon/>}
          >
            {languages.en.logout}
          </Button>
        </Toolbar>
      </Container>
    </AppBar>
  );
};

export default Header;
