import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import ClientForm from "./ClientForm";
import { useEffect, useReducer } from "react";
import axios from "axios";
import globalVars from "../library/globalVariables";

const CreateClient = ({
  fields,
  isClientAdded,
  toggleIsClientAdded,
  refreshList
}: any) => {
  const initialNewClintState = fields
    .map((e: any) => e.fieldName)
    .reduce((o, key) => Object.assign(o, { [key]: "" }), {});

  const [newClient, dispatch] = useReducer((state, action) => {
    switch (action.type) {
      case "onNumberChange":
        return {
          ...state,
          [action.fieldName]: parseInt(action.event.target.value)
        };

      case "onTextChange":
        return { ...state, [action.fieldName]: action.event.target.value };

      case "clear":
        return initialNewClintState;

      default:
        return state;
    }
  }, initialNewClintState);

  const onChange = (fieldName, fieldType, event) => {
    fieldType === "number"
      ? dispatch({
          type: "onNumberChange",
          fieldName,
          event
        })
      : dispatch({
          type: "onTextChange",
          fieldName,
          event
        });
  };

  const onSubmit = async e => {
    e.preventDefault();
    const clientRes = await axios({
      method: "post",
      data: newClient,
      url: `${globalVars.serverURL}/clients`,
      responseType: "json"
    });
    const clientData = await clientRes.data;
    if (clientData.msg === "Success") {
      dispatch({
        type: "clear"
      });
      toggleIsClientAdded();
      refreshList();
    }
  };

  const onCancel = () => {
    dispatch({
      type: "clear"
    });
    toggleIsClientAdded();
  };

  useEffect(() => {
    // console.log(newClient);
  });

  return isClientAdded ? (
    <div>
      <Typography variant="h4" component="h2" gutterBottom>
        Add new client
      </Typography>
      <form onSubmit={onSubmit}>
        <ClientForm fields={fields} onChange={onChange} newClient={newClient} />
        <Button variant="contained" color="primary" type="submit">
          Save
        </Button>
        <Button variant="contained" color="secondary" onClick={onCancel}>
          Cancel
        </Button>
      </form>
    </div>
  ) : null;
};

export default CreateClient;
