import StringMethods from "../library/stringMethods";

interface IProps {
    sortBy: any
    fields: any;
  }

const TableHead = ({fields, sortBy} : IProps) => {

    const tableHeadings = fields.map((e : any) => {
        const tableHeading = new StringMethods(e.fieldName).camelStringToText().firstCharUpperCase().getString()

        return <th key={tableHeading}><button onClick={() => sortBy(e.fieldName)}>{tableHeading}</button></th>;
    })

    return (
        <tr>
            {tableHeadings}
        </tr>
    )
}

export default TableHead;
