import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import ClientForm from "./ClientForm";
import { useReducer, useEffect } from "react";
import axios from "axios";
import globalVars from "../../library/globalVariables";
import generateUniqueId from "generate-unique-id";
import moment from "moment";

const CreateClient = ({
  fields,
  isClientAdded,
  toggleIsClientAdded,
  addNewClientToState,
}: any) => {
  // const initialNewClintState = fields
  //   .map((e: any) => e.fieldName)
  //   .reduce((o, key) => Object.assign(o, { [key]: "" }), {});

  const initialNewClientState = () => {
    let clientObj = fields
    .map((e: any) => e.fieldName)
    .reduce((o, key) => Object.assign(o, { [key]: "" }), {});
    clientObj._id = generateUniqueId({
      length: 24,
      useNumbers: true,
      useLetters: false,
      includeSymbols: ["a", "b", "c", "d", "e", "f"]}),
    clientObj.dateAdded = moment().format("llll")

    return clientObj;
  }

  const [newClient, setNewClient] = useReducer((state, action) => {
    switch (action.type) {
      case "onTextChange":
        return {
          ...state,
          [action.payload.fieldName]:
            action.payload.fieldType === "number"
              ? parseInt(action.payload.value)
              : action.payload.value
        };
      case "addId":
        return {
          ...state,
          _id: generateUniqueId({
            length: 24,
            useNumbers: true,
            useLetters: false,
            includeSymbols: ["a", "b", "c", "d", "e", "f"]
          })
        };
      case "addDate":
        return {
          ...state,
           dateAdded: moment().format("llll")
        }
      case "clear":
        return {

        };

      default:
        return state;
    }
  }, initialNewClientState());

  useEffect(() => {
    console.log(newClient);
  }, [newClient])

  const onChange = (fieldType, event) => {
    setNewClient({
      type: "onTextChange",
      payload: { fieldName : event.target.name, value: event.target.value, fieldType }
    });

  };

  const onSubmit = async e => {
      e.preventDefault();

      const clientRes = await axios({
        method: "post",
        data: newClient,
        url: `${globalVars.serverURL}/clients`,
        responseType: "json"
      });
      const clientData = await clientRes.data;
      if (clientData.msg === "Success") {
        addNewClientToState(newClient);
        setNewClient({
          type: "clear"
        });
        toggleIsClientAdded();
      }
  };

  const onCancel = () => {
    setNewClient({
      type: "clear"
    });
    toggleIsClientAdded();
  };

  return isClientAdded ? (
    <div>
      <Typography variant="h4" component="h2" gutterBottom>
        Add new client
      </Typography>
      <form onSubmit={onSubmit}>
        <ClientForm fields={fields} onChange={onChange} newClient={newClient} />
        <Button variant="contained" color="primary" type="submit">
          Save
        </Button>
        <Button variant="contained" color="secondary" onClick={onCancel}>
          Cancel
        </Button>
      </form>
    </div>
  ) : null;
};

export default CreateClient;
