import { useReducer, useEffect, useState } from "react";
import WebFormSelect from "./WebFormSelect";
import WebFormList from "./WebFormList";
import WebFormOptionSelect from "./webFormOptionSelect";

const WebForm = ({ fields }) => {
  const [optionSelect, setOptionSelect] = useState("");
  const [webFields, setWebFields] = useReducer((state, action) => {
    switch (action.type) {
      case "add":
        return state.map(field =>
          field._id === action.payload.id
            ? { ...field, fieldInForm: true }
            : field
        );
      case "remove":
        return state.map(field =>
          field._id === action.payload.id
            ? { ...field, fieldInForm: false }
            : field
        );
      case "addVisibleSelect": 
            return state
      default:
        return state;
    }
  }, fields);

  const addNotSelect = e => {
    console.log(e.target);
    webFields.map(field =>
      field._id === e.target.id
        ? field.fieldType !== "select"
          ? setWebFields({
              type: "add",
              payload: { id: e.target.id }
            })
          : setOptionSelect(e.target.id)
        : null
    );
  };

  const addVisibleSelect = e => {
    setWebFields({
      type: "addVisibleSelect",
      payload: { id: e.target.id }
    });
  };

  const removeFromList = e => {
    setWebFields({
      type: "remove",
      payload: { id: e.target.id }
    });
  };

  useEffect(() => {
    console.log(webFields);
  }, [webFields]);

  return (
    <>
      <h3>Select field:</h3>
      <WebFormSelect
        webFields={webFields}
        addNotSelect={addNotSelect}
        // optionSelect={optionSelect}
      />
      <WebFormOptionSelect optionSelect={optionSelect} webFields={webFields} addVisibleSelect={addVisibleSelect} />
      <h3>Selected fields:</h3>
      <WebFormList webFields={webFields} removeFromList={removeFromList} />
    </>
  );
};

export default WebForm;
