import Box from '@material-ui/core/Box';
import CircularProgress from '@material-ui/core/CircularProgress';

interface LoadingSpinnerProps {
    position ?: string;
    level ?: number;
}

const LoadingSpinner = ({style}: any) => {

    const {position, level}:LoadingSpinnerProps = style


    const styleSpinner = () => {
        switch(position) {
            case `mr-${level}`:
                return {marginRight: `${level}rem`}
        }
    }

    return (
        <Box style={styleSpinner()}>
            <CircularProgress />
        </Box>
    )
}

export default LoadingSpinner;
