import { useEffect, useState } from "react";

const SelectFieldOptions = ({ inputsNumber, getInputs }: any) => {
  const [ options, setOptionObject ] = useState([]);

  useEffect(() => {
    getInputs(optionValue)
  })

  const onChange = (e : any) => {
    setOptionObject(optionValue[e.target.id].push({text: e.target.value, id: e.target.id}))
    console.log(optionValue);
  }

  let items: any = [];

    for (let i = 0; i < inputsNumber; i++) {
      items.push(
        <div key={i}>
          <input type="text" onChange={onChange} value={options[i].text} id={i.toString()}/>
          <button id={i.toString()}>
            X
          </button>
        </div>
      );
    }

  return <div>{items}</div>;
};
export default SelectFieldOptions;
