import Link from "next/link";
import uniqid from "uniqid";

interface IProps {
  clients: any;
  reverse: boolean;
  sort: string;
  fields: any;
}

const ClientsList = ({ clients, reverse, sort, fields}: IProps) => {
  reverse
    ? clients.sort((a: any, b: any) => (b[sort] > a[sort] ? 1 : -1))
    : clients.sort((a: any, b: any) => (b[sort] > a[sort] ? -1 : 1));


  const fieldNames = [];
  fields.forEach((e : any) => fieldNames.push(e.fieldName));

  const tableClients = clients.map((e: any) => {
    const tableItem = () => {
      let items = [];
      for (let key in e) {
        e[key] !== e._id && fieldNames.includes(e[key]) ? items.push(<td key={uniqid()}>{e[key]}</td>) : null;
      }
      return items;
    };

    return (
      <tr key={e._id}>
        {tableItem()}
        <td>
          <Link href="/clients/[id]" as={`/clients/${e._id}`}>
            <a>Go to client</a>
          </Link>
        </td>
      </tr>
    );
  });
  return tableClients;
};

export default ClientsList;
