import { useState, useEffect } from "react";
import { useRouter } from "next/router";
import axios from "axios";
import Header from "../components/Header";
import ClientsList from "../components/ClientList";
import stringMethods from "../library/stringMethods";
import titleSubText from "../library/globalVariables";



//TODO: TABULKA DYNAMICKÉ ZÁHLAVÍ
//TODO: PŘIDAT NOVÉHO KLIENTA


const Clients = (props: any) => {
  const router = useRouter();

  const [clients, setClientList] = useState(props.data);
  const [reverse, setReverseOrder] = useState(false);

  useEffect(() => {
    const title = new stringMethods(router.pathname)
      .removeSlash()
      .firstCharUpperCase()
      .addStringToEnd(titleSubText)
      .text;
    document.title = title;
  }, [router]);

  const reverseOrder = () => {
    reverse === false ? setReverseOrder(true) : setReverseOrder(false);
  }

  if (clients.length === 0) {
    return "Loading";
  }
  return (
    <div>
      <Header />
      <h1>All clients</h1>
      <table>
        <thead>
          <tr>
            <th><button onClick={reverseOrder}>Date Added</button></th>
            <th><button>Name</button></th>
            <th>Age</th>
            <th>Address</th>
          </tr>
        </thead>
        <tbody>
          <ClientsList clients={clients} reverse={reverse} sortBy={sortBy}/>
        </tbody>
      </table>
    </div>
  );
};

Clients.getInitialProps = async () => {
  const res = await axios({
    method: "get",
    url: "http://localhost:8080/api/clients",
    responseType: "json"
  });
  const data = await res.data;
  return {
    data
  };
};

export default Clients;
