import { useState, useContext } from "react";
import axios from "axios";
import UserContext from "../UserContext";
import TextInput from "./TextInput";
import LoginButton from "./Button";
import globalVars from "../../library/globalVariables";
import languages from "../../library/languages";
import stringMethods from "../../library/stringMethods";

const RegisterForm = () => {
    const [user, setUser] = useState({username : "", password : "" });
    const userContext = useContext(UserContext);

    const onChange = (e) =>Â {
        e.target.name === "username"
            ? setUser({...user, username: e.target.value})
            : setUser({...user, password: e.target.value})
    }

    const onRegister = async() => {
        const userRes = await axios({
            method: "POST",
            data: user,
            url: `${globalVars.serverURL}/users`,
            responseType: "json"
        })
        const userData = await userRes.data;
        if(userData.msg === "Success") {
            userContext.setUser(user.username);
        } else {
            alert("Incorrect username or password")
        }
    }

    return (
        <>  
            <h2>Register</h2>
            <form>
                <TextInput onChange={onChange} type="text" value={user.username} text="username" title={new stringMethods(languages.en.username).firstCharUpperCase().getString()} />
                <TextInput onChange={onChange} type="password" value={user.password} text="password" title={languages.en.password} />
                <TextInput onChange={onChange} type="password" value={user.password} text="password" title={new stringMethods(languages.en.repeatPassword).firstCharUpperCase().getString()}/>
                <LoginButton onClick={onRegister} text={languages.en.register} />
            </form>
        </>
    )
}

export default RegisterForm;
