import Header from "../../components/Header";
import axios from "axios";
import { useEffect, useState} from "react";
import stringMethods from "../../library/stringMethods";
import globalVars from "../../library/globalVariables";

//TODO: SKRÝVÁNÍ POLÍ CO NEJSOU V DB

const Client = ({ clientData, fieldsData }: any) => {
  const [client, setEditedClient] = useState(clientData);
  const { firstName, lastName } = client;

  useEffect(() => {
    document.title = `${firstName} ${lastName} ${globalVars.titleSubText}`;
    // console.log(clientData);
    // console.log(fieldsData);
  });

  const showAllProperities = () => {
    const list = [];
    fieldsData.map(field => {
      switch (field.fieldType) {
        case "text":
          list.push(
            <li key={field.fieldName}>
              {new stringMethods(field.fieldName)
                .camelStringToText()
                .firstCharUpperCase()
                .getString()}
              <textarea value={client[field.fieldName]} />
            </li>
          );
          break;
        case "number":
          list.push(
            <li key={field.fieldName}>
              {new stringMethods(field.fieldName)
                .camelStringToText()
                .firstCharUpperCase()
                .getString()}
              <input type="number" value={client[field.fieldName]} />
            </li>
          );
          break;
        case "select":
          list.push(
            <li key={field.fieldName}>
              {new stringMethods(field.fieldName)
                .camelStringToText()
                .firstCharUpperCase()
                .getString()}
              <select value={client[field.fieldName]}>
                {field.fieldOptions.map(option => (
                  <option key={option.id}>{option.value}</option>
                ))}
              </select>
            </li>
          );
      }
    });
    return list;
  };

  return (
    <div>
      <Header />
      <h1>
        {firstName} {lastName}
      </h1>
      <ul>{showAllProperities()}</ul>
    </div>
  );
};

Client.getInitialProps = async (context: any) => {
  const { id } = context.query;
  const resClient = await axios({
    method: "get",
    url: `${globalVars.serverURL}/clients/${id}`,
    responseType: "json"
  });
  const clientData = await resClient.data;

  const resFields = await axios({
    method: "get",
    url: `${globalVars.serverURL}/fields`,
    responseType: "json"
  });
  const fieldsData = await resFields.data;

  return { clientData, fieldsData };
};

export default Client;
