import uniqid from "uniqid";
import { useEffect } from "react";

const ClientForm = ({ fields, onChange, newClient }: any) => {
  useEffect(() => {
    console.log("rerendered");
  });

  return fields.map((e: any) => {
    if (e.fieldName !== "dateAdded" && e.fieldName !== "dateModified") {
      if (e.fieldType === "text") {
        return (
          <div key={e.fieldName}>
            <p>{e.fieldName}</p>
            <input
              type="text"
              id={e.fieldName}
              onChange={() => onChange(e.fieldName, event)}
              value={newClient[e.fieldName]}
            />
          </div>
        );
      } else if (e.fieldType === "number") {
        return (
          <div key={e.fieldName}>
            <p>{e.fieldName}</p>
            <input type="number" id={e.fieldName} />
          </div>
        );
      } else if (e.fieldType === "select") {
        return (
          <div key={e.fieldName}>
            <p>{e.fieldName}</p>
            <select id={e.fieldName}>
              {e.fieldOptions.map((e: any) => {
                return (
                  <option value={e.value} key={uniqid()}>
                    {e.value}
                  </option>
                );
              })}
            </select>
          </div>
        );
      }
    }
  });
};

export default ClientForm;
