import axios from "axios";
import { useState, useRef } from "react";
import globalVars from "../../library/globalVariables";
import languages from "../../library/languages";
import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import { lang } from "moment";

const EmailSettings = ({ data }: any) => {
  const [emailSettings, setEmailSettings] = useState(data);
  const [isPassShown, setIsPassShown] = useState(false)
  const passEl = useRef(null);

  const onSubmit = async (e) => {
    e.preventDefault();

    const {username, pass} = emailSettings;

    const res = await axios({
      method: "post",
      url: `${globalVars.serverURL}/emails`,
      data: {username, pass},
      responseType: "json"
    });
    const data = res.data;
    if (data === "Success") {
      alert(languages.en.emailSettingsUpdatedSuccess);
    } else {
      alert(languages.en.somethingWentWrong);
    }
  };

  const showPass = (e) => {
      e.preventDefault();
      passEl.current.type = "text";
      setIsPassShown(true);
  }

  const onChange = e => {
    setEmailSettings({ ...emailSettings, [e.target.name]: e.target.value });
  };
  return (
    <>
    <Typography variant="h4" component="h2" gutterBottom>{languages.en.emailAccount}</Typography>
    <form onSubmit={onSubmit}>
      <label htmlFor="acc-name">{languages.en.email}</label>
      <input
        id="acc-name"
        type="text"
        name="username"
        value={emailSettings.username}
        onChange={onChange}
      />
      <label htmlFor="acc-pass">{languages.en.password}</label>
      <input
        id="acc-pass"
        type="password"
        name="pass"
        ref={passEl}
        value={emailSettings.pass}
        onChange={onChange}
      />
      <Button variant="contained" color="primary" onClick={showPass} disabled={isPassShown}>{languages.en.showPassword}</Button>
      <Button variant="contained" color="primary" type="submit">Save</Button>
    </form>
    </>
  );
};

export default EmailSettings;
