const SelectFieldOptions = ({ options, onOptionChange, onOptionDelete, fieldMethods }: any) => {
  let items = options.map((e: any) => {
    return (
      <div key={e.id}>
        <input type="text" onChange={onOptionChange} id={e.id} defaultValue={e.value} />
        <button id={e.id} onClick={onOptionDelete}>
          X
        </button>
      </div>
    );
  });

  return <div>{items}<button onClick={fieldMethods.handleOptionSpawn}>New</button></div>;
};
export default SelectFieldOptions;
