import StringMethods from "../library/stringMethods";

interface IProps {
  sortBy: any;
  fields: any;
  reverse: boolean;
}

const TableHead = ({ fields, sortBy, reverse }: IProps) => {
  const tableHeadings = fields.map((e: any) => {
    const tableHeading = new StringMethods(e.fieldName)
      .camelStringToText()
      .firstCharUpperCase()
      .getString();

    return (
      <th key={tableHeading}>
        {reverse ? "<" : ">"}<button onClick={() => sortBy(e.fieldName)}>{tableHeading}</button>
      </th>
    );
  });

  return (
    <thead>
      <tr>{tableHeadings}</tr>
    </thead>
  );
};

export default TableHead;
