import Header from "../../components/Header";
import axios from "axios";
import { useEffect } from "react";
// import stringMethods from "../../library/stringMethods";
import globalVars from "../../library/globalVariables";

//TODO: SKRÝVÁNÍ POLÍ CO NEJSOU V DB

const Client = ({ clientData, fieldsData }: any) => {
  const { firstName, lastName } = clientData;

  useEffect(() => {
    document.title = `${name} ${globalVars.titleSubText}`;
    // console.log(clientData);
    // console.log(fieldsData);
  });

  const showAllProperities = () => {
    const list = [];

    fieldsData.map(field => {
      for (let key in clientData) {
        // const field.fieldName = new stringMethods(key)
        //   .camelStringToText()
        //   .firstCharUpperCase()
        //   .getString();
          switch (field.fieldType) {
            case "text":
              list.push(
                <li key={field.fieldName}>
                  {field.fieldName}
                  <textarea value={clientData[key]} />
                </li>
              );
              break;
            case "number":
              list.push(
                <li key={field.fieldName}>
                  {field.fieldName}:<input type="number" value={clientData[key]} />
                </li>
              );
            break;
            case "select":
              list.push(
                <li key={key}>
                  {field.fieldName}:<select value={clientData[key]}></select>
                </li>
              )
          }
      }
    });
    return list;
  };

  return (
    <div>
      <Header />
      <h1>
        {firstName} {lastName}
      </h1>
      <ul>{showAllProperities()}</ul>
    </div>
  );
};

Client.getInitialProps = async (context: any) => {
  const { id } = context.query;
  const resClient = await axios({
    method: "get",
    url: `${globalVars.serverURL}/clients/${id}`,
    responseType: "json"
  });
  const clientData = await resClient.data;

  const resFields = await axios({
    method: "get",
    url: `${globalVars.serverURL}/fields`,
    responseType: "json"
  });
  const fieldsData = await resFields.data;

  return { clientData, fieldsData };
};

export default Client;
