import Link from 'next/link';

interface IProps {
    clients: any,
    reverse: boolean,
    sortBy: string
  }

const ClientsList = ({clients, sortBy, reverse} : IProps) => {
    if (sortBy === "age") {
        return clients.sort((a : any, b : any) => b.age > a.age ? -1 : 1);
    }

    const tableClients = sort.map((e:any) => (
      <tr key={e._id}>
        <td>{e.dateAdded}</td>
        <td>{e.name}</td>
        <td>{e.age}</td>
        <td>{e.address}</td>
        <td>
          <Link href="/clients/[id]" as={`/clients/${e._id}`}>
            <a>Go to client</a>
          </Link>
        </td>
      </tr>
    ));
    return tableClients;
    // return reverse ? tableClients.reverse() : tableClients;
  };

  export default ClientsList;
