import axios from "axios";
import globalVars from "../library/globalVariables";
import { useState, useEffect } from "react";

const EmailForm = ({to} : any) => {
    const [email, setEmail] = useState({to});

    useEffect(() => {
        console.log(email);
    })

    const sendEmail = async(e) => {
        e.preventDefault();
        const res = await axios({
            method: "post",
            data: email,
            url: `${globalVars.serverURL}/emails/send`,
            responseType: "json"
        })
        const data = res.data;
        data.msg === "Success" ? alert("Your mail has been succesfully sent.") : alert("Something went wrong!")
    }

    const onChange = (e) => {
        setEmail({...email, [e.target.name] : e.target.value})
    }

    return (
        <form onSubmit={sendEmail}>
            <input type="text" name="to" onChange={onChange} readOnly={to.length > 0 ? true : false} defaultValue={to} />
            <input type="text" name="subject" onChange={onChange} />
            <textarea name="message" onChange={onChange}/>
            <button>Cancel</button>
            <button type="submit">Send</button>
        </form>
    )
}

export default EmailForm;
