import axios from "axios";
import { useState } from "react";

const EmailSettings = ({ data }: any) => {
    const [emailSettings, setEmailSettings] = useState(data);

  const onSubmit = async () => {
      const res = await axios({
        method: "post",
        data: emailSettings,
        responseType: "json",
      })
      const data = res.data;
      if (data === "Success") {
          alert("Your email settings has been successfully changed!")
      } else {
          alert("Something went wrong!")
      }
  };
  return (
    <form onSubmit={onSubmit}>
      <input type="text" value={emailSettings.username} />
      <input type="password" value={emailSettings.pass} />
      <button>Save</button>
    </form>
  );
};

export default EmailSettings;
