const webFormOptionSelect = ({ optionSelect, webFields, addVisibleSelect }) => {
  // vem vybrany select
  // nabidni moznost jestli pridat do formu -> ano -> pridat do listu se stringem -> do stavu
  // ne -> zobraz druhy select s options + string -> vloz do listu se stringem -> do stavu
  const [thisField, setThisField] = useState()

  const insertIntoForm = webFields.map(field =>
    optionSelect === field.fieldName ? (
      <div key={field.id}>
        <p>
          Should be <strong>{field.fieldName}</strong> visible in form?
        </p>
        <button id={field.fieldName} onClick={addVisibleSelect}>
          YES
        </button>
        <button id={field.fieldName} onClick={() => setThisField(field)}>
          NO
        </button>
      </div>
    ) : null
  );

  return (
    <>
      {insertIntoForm}
      <select><WebFormOptions field={field} /></select>
    </>
  );
};

export default webFormOptionSelect;
