import Button from "@material-ui/core/Button";
import Typography from "@material-ui/core/Typography";
import ClientForm from "./ClientForm";
import { useEffect } from "react";
import { useState } from "react";

const CreateClient = ({fields} : any) => {

    const initialNewClintState = fields.map((e : any) => {{e.fieldName}})
    const [newClient, setNewClient] = useState(initialNewClintState);

    const onChange = (e) => {
        setNewClient({...newClient, fieldName: e.target.value})
    }

    useEffect(() => {
       console.log(newClient);
    }, [])

    return (
        <div>
            <Typography variant="h4" component="h2" gutterBottom>Add new client</Typography>
            <ClientForm fields={fields} onChange={onChange} newClient={newClient}/>
            <Button variant="contained" color="primary">Save</Button>
            <Button variant="contained" color="secondary">Cancel</Button>
        </div>
    )
}

export default CreateClient;
