import WebFormSelect from "./WebFormSelect";
import { useReducer } from "react";

const WebForm = ({ fields }) => {
  const [webFields, setWebFields] = useReducer((state, action) => {
    switch (action.type) {
      case "add": 
        return state.map(field => field.fieldName === action.payload)
      default:
        return state;
    }
  }, fields);

  const addedToForm = (e) => {
    setWebFields({
        type: "add",
        payload: {fieldName : e.target.value}
    })
  }

  return (
    <>
      <WebFormSelect webFields={webFields} addedToForm={addedToForm} />
    </>
  );
};

export default WebForm;
