import uniqid from "uniqid";

const ClientForm = ({ fields, onChange, newClient }: any) => {

    const handleChange = () =>Â {

    }

  const inputs = fields.map((e: any) => {
    if (e.fieldName !== "dateAdded" && e.fieldName !== "dateModified") {
      const inputType = () => {
        if (e.fieldType === "text") {
          return <input type="text" id={e.fieldName} onChange={onChange(e.fieldName, event)} value={newClient[e.fieldName]}/>;
        } else if (e.fieldType === "number") {
          return <input type="number" id={e.fieldName}/>;
        } else if (e.fieldType === "select") {
          return (
            <select id={e.fieldName}>
              {e.fieldOptions.map((e: any) => {
                return (
                  <option value={e.value} key={uniqid()}>
                    {e.value}
                  </option>
                );
              })}
            </select>
          );
        }
      };
      return (
        <div key={uniqid()}>
          <p>{e.fieldName}</p>
          {inputType()}
        </div>
      );
    }
  });

  return <div>{inputs}</div>;
};

export default ClientForm;
