import { useReducer, useEffect, useState } from "react";
import WebFormSelect from "./WebFormSelect";
import WebFormList from "./WebFormList";
import WebFormOptionSelect from "./webFormOptionSelect";

const WebForm = ({ fields }) => {
  const [optionSelect, setOptionSelect] = useState(false);
  const [webFields, setWebFields] = useReducer((state, action) => {
    switch (action.type) {
      case "add":
        return state.map(field =>
          field.fieldName === action.payload.fieldName
            ? { ...field, fieldInForm: true }
            : field
        );
      case "remove":
        return state.map(field =>
          field.fieldName === action.payload.fieldName
            ? { ...field, fieldInForm: false }
            : field
        );
      default:
        return state;
    }
  }, fields);

  const addedToForm = e => {
    setWebFields({
      type: "add",
      payload: { fieldName: e.target.value }
    });
  };

  const removeFromList = e => {
    setWebFields({
      type: "remove",
      payload: { fieldName: e.target.id }
    });
  };

  useEffect(() => {
    console.log(webFields);
  }, [webFields]);

  return (
    <>
      <h3>Select field:</h3>
      <WebFormSelect webFields={webFields} addedToForm={addedToForm} optionSelect={optionSelect}/>
      <WebFormOptionSelect optionSelect={optionSelect} />
      <h3>Selected fields:</h3>
      <WebFormList webFields={webFields} removeFromList={removeFromList} optionSelect={optionSelect} />
    </>
  );
};

export default WebForm;
