class stringMethods {
  text: string;

  constructor(text: string) {
    this.text = text;
  }

  splitCamelString = () => {
    this.text = Array.from(this.text)
      .map(e => (e === e.toUpperCase() ? (e = `%-%${e.toLowerCase()}`) : e))
      .join("")
      .replace(/%-%/g, " ");

    return this;
  };

  firstCharUpperCase = () => {
    this.text = Array.from(this.text)
