import axios from "axios";
import { useState, useEffect, useRef } from "react";

const EmailSettings = ({ data }: any) => {
  const [emailSettings, setEmailSettings] = useState(data);
  const passEl = useRef(null);

  useEffect(() => {
      console.log(emailSettings);
  })

  const onSubmit = async () => {
    const res = await axios({
      method: "post",
      data: emailSettings,
      responseType: "json"
    });
    const data = res.data;
    if (data === "Success") {
      alert("Your email settings has been successfully changed!");
    } else {
      alert("Something went wrong!");
    }
  };

  const showPass = (e) => {
      e.preventDefault();
      passEl.current.type = "text";
  }

  const onChange = e => {
    setEmailSettings({ ...emailSettings, [e.target.name]: e.target.value });
  };
  return (
    <form onSubmit={onSubmit}>
      <input
        type="text"
        name="username"
        value={emailSettings.username}
        onChange={onChange}
      />
      <input
        type="password"
        name="pass"
        ref={passEl}
        value={emailSettings.pass}
        onChange={onChange}
      />
      <button onClick={showPass}>Show Password</button>
      <button type="submit">Save</button>
    </form>
  );
};

export default EmailSettings;
