import { useState } from "react";
import AddOrEditField from "../components/AddOrEditField";
import axios from "axios";
import globalVars from "../library/globalVariables";
import CustomFields from "../components/CustomFields";

const CustomClientFields = ({ fields, refreshList }: any) => {
  const [displayComponent, setDisplayComponent] = useState(false);
  const [editedField, setEditedField] = useState({
    fieldName: "",
    fieldType: "text",
    fieldPermanent: false,
    fieldOptions: []
  })

  const deleteField = async (event : any) => {
    const id = event.target.id;
    const res = await axios({
        method: "delete",
        url:`${globalVars.serverURL}/fields/${id}`,
        responseType: "json"
    })
    const resData = await res.data;
    resData.msg === "Success" ? refreshList() : null;
  }

  const changeDisplayComponent = (obj ? : any) => {
    if (obj) {
      setEditedField(obj);
    }
    displayComponent ? setDisplayComponent(false) : setDisplayComponent(true);
    //TODO: POSÍLAT PO KLIKU NA EDIT FIELD
  }

  return (
    <div>
      <h2>Custom Client Fields</h2>
      <CustomFields deleteField={deleteField} changeDisplayComponent={changeDisplayComponent} fields={fields}/>
      {!displayComponent ? <button onClick={changeDisplayComponent}>Add new field</button> : <AddOrEditField fieldObject={editedField} changeDisplayComponent={changeDisplayComponent} refreshList={refreshList} />}
    </div>
  );
};

export default CustomClientFields;
